import { useEffect, useState } from "react";
import { DotPulse } from "@uiball/loaders";
//import axios from "axios";
import Card from "../Card/Card";
import { useSelector, useDispatch } from "react-redux";
import "./App.css";
import SearchBar from "../Search/SearchBar";
import { getAllGames } from "../../redux/actions/actions";
//const API = "http://localhost:3001/videogames/";

const notFullGames = (r) => {
  if ((r.length) === 0) {
    return (
      <div>
        <h3>Ya no hay videojuegos</h3>
      </div>
    );
  }
};

const Home = () => {
  const [isTrue, setIsTrue] = useState(false);
  const allVideogames = useSelector((state) => state.allVideogames);
  const dispatch = useDispatch();

  //paginacion
  const [currentPage, setCurrentPage] = useState(1);
  const gamesPerPage = 15;
  const lastIndex = currentPage * gamesPerPage;
  const firtsIndex = lastIndex - gamesPerPage;
  const records = allVideogames.slice(firtsIndex, lastIndex);
  const numPage = Math.ceil(allVideogames.length / gamesPerPage);
  const numbers = [...Array(numPage + 1).keys()].slice(1);

  const prePage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const changeToPage = (id) => {
    setCurrentPage(id);
  };

  const nextPage = () => {
    if (currentPage !== numPage) {
      setCurrentPage(currentPage + 1);
    }
  };

  useEffect(() => {
    setIsTrue(true);
    dispatch(getAllGames());
    if (allVideogames.lenght > 0) {
      setIsTrue(false);
    }
  }, [dispatch, allVideogames.length]);

  return (
    <div className="App">
      <SearchBar />
      <nav>
        <ul>
          <li>
            <button type="button" onClick={prePage}>
              Prev
            </button>
          </li>
          {numbers.map((n, i) => (
            <li key={i}>
              <span onClick={() => changeToPage(n)}>{n}</span>
            </li>
          ))}
          <li>
            <button type="button" onClick={nextPage}>
              Next
            </button>
          </li>
        </ul>
      </nav>
      {isTrue ? (
        <main>
          {records?.map((game) => {
            return (
              <Card
                key={game.id}
                id={game.id}
                name={game.nombre}
                image={game.imagen}
                plataforms={game.plataformas}
                genero={game.genero}
              />
            );
          })}
        </main>
      ) : (
        <div>
          <DotPulse size={80} lineWeight={3.5} speed={1} color="white" />
        </div>
      )}
      {notFullGames(records)}
    </div>
  );
};

export default Home;
