import React from "react";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import style from "./Detail.module.css";
import Loading from "../Loading/Loading";

const Detail = () => {
  const [detail, setDetail] = useState({});
  const { id } = useParams();

  const getDetail = async (id) => {
    try {
      const { data } = await axios.get(
        `http://localhost:3001/videogames/${id}`
      );
      console.log(data);
      if (data.nombre) {
        setDetail(data);
      } else {
        window.alert("No se encontro el ID");
      }
    } catch (error) {
      console.log("Error detail axios: " + error.message);
    }
  };

  useEffect(() => {
    getDetail(id);
  }, [id]);

  return (
    <div className={style.containerDetail}>
      <div className={style.detailTitle}>
        <h2>DETAIL VIDEOGAME</h2>{" "}
      </div>
      <Link className={style.btnHome} to={"/home"}>
        To Home
      </Link>

      {detail.id ? (
        <div className={style.cardDetail}>
          <div>
            <h2>{detail.nombre}</h2>
            <div className={style.descripcionStyle}> 
              <h3>Descripcion: </h3>
              {
                <h5>
                  {detail.descripcion
                    .replace(/<p>/g, "")
                    .replace(/<\/p>/g, "")
                    .split(".")
                    .slice(0, 3)
                    .join(".")}
                </h5>
              }
            </div>

            <div className={style.fechaLanza}>
              <h3>Fecha de lanzamiento: </h3>
              <h3>{detail.fecha_de_lanzamiento}</h3>
            </div>

            <div className={style.generoStyle}>
              <h3>Genero: </h3>
              <div>
                {Array.isArray(detail.genero) ? (
                  detail.genero?.map((genr) => (
                    <h3 key={genr.id}>{genr.name}</h3>
                  ))
                ) : (
                  <h3>{detail.genero}</h3>
                )}
              </div>
            </div>

            <div className={style.platStyle}>
              <h3>Plataformas: </h3>

              {Array.isArray(detail.plataformas) ? (
                detail.plataformas?.map((plataforma, index) => {
                  return <h3 key={index}>{plataforma}</h3>;
                })
              ) : (
                <h3>{detail.plataformas}</h3>
              )}
            </div>
          </div>

          <img
            src={detail.imagen}
            width="300px"
            height="280px"
            alt={detail.nombre}
          />
        </div>
      ) : (
        <div>
          <Loading />
        </div>
      )}
    </div>
  );
};

export default Detail;
