import "./CardStyle.css";
import { Link } from "react-router-dom";
import { Pinwheel } from "@uiball/loaders";

const Card = (props) => {
  const { name, image, id, plataforms, genero } = props;

  return (
    <>
      {props.plataforms ? (
        <div className="container" key={id}>
          <main className="wrapper">
            <img
              className="banner-image"
              width="200px"
              height="170px"
              src={image}
              alt={name}
            />
            <div>
              <h1>{name}</h1>
            </div>

            <div className="containArrays">
              <div>
                {Array.isArray(plataforms) ? (
                  plataforms
                    ?.map((elem, index) => {
                      return <p key={index}>{elem}</p>;
                    })
                    .slice(0, 4)
                ) : (
                  <p>{plataforms}</p>
                )}
              </div>
              <div>
                {Array.isArray(genero) ? (
                  genero?.map((genre, index) => {
                    return <h4 key={index}>{genre}</h4>;
                  })
                ) : (
                  <h4>{genero}</h4>
                )}
              </div>
            </div>
          </main>
          <footer className="button-wrapper">
            <Link to="/detail/1">
              <button className="btn-outline">DETAIL</button>
            </Link>

            <button className="btn-fill">DELETE</button>
          </footer>
        </div>
      ) : (
        <Pinwheel size={35} lineWeight={3.5} speed={1} color="black" />
      )}
    </>
  );
};

export default Card;
