import React, { useState } from "react";
import { Link } from "react-router-dom";

const SearchBar = () => {
  const [searchName, setSearchName] = useState("");
  const [searchId, setSearchId] = useState("");

  const handleSearch = (e) => {
    if (e.target.name === "search_id") {
      setSearchId(e.target.value);
    } else if (e.target.name === "search_name") {
      setSearchName(e.target.value);
    }
  };

  const handleSubmitId = (e) => {
    e.preventDefault();

    console.log("Búsqueda por ID:", searchId);
   setSearchId("")
  };

  const handleSubmitName = (e) => {
    e.preventDefault();

    console.log("Busqueda por Name:", searchName);
    setSearchName("")
  }

  return (
    <>
      <nav style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
        <form>
          {" "}
          <div className="search-container">
            <input
              type="search"
              name="search_id"
              onChange={handleSearch}
              placeholder="Buscar por ID"
              value={searchId}
            />
            <button onClick={handleSubmitId}>Búsqueda por ID</button>
          </div>
          <div className="search-container">
            <input
              type="search"
              name="search_name"
              onChange={handleSearch}
              placeholder="Buscar por nombre"
              value={searchName}
            />
            <button onClick={handleSubmitName} >Búsqueda por nombre</button>
          </div>
          <div className="nav-links">
            <Link to="/home">
              <button>Home</button>
            </Link>
            <Link to="/create">
              <button>Crear</button>
            </Link>
            <Link to="/update">
              <button>Actualizar</button>
            </Link>
          </div>
        </form>
      </nav>
    </>
  );
};

export default SearchBar;
