import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {getAllGenres} from "../../redux/actions/actions"


const Filters = ({filteredVideogames, onChangeFilters}) => {

    const dispacth = useDispatch();
    const {allGenres} = useSelector((state) => state);

    //aqui voy a despachar la accion que me va a trear los generos del estado global
    //ma traigo el estado AllGenres y AllPlataforms
    useEffect(() => {
      dispacth(getAllGenres());

    }, [dispacth]);

    const handleChangeGenre = (e) => {
        onChangeFilters(prevState =>({
            ...prevState,
            genero: e.target.value
        }))
    }


    return(
        <>
        <section>
            <div>
                <label htmlFor="genero">Genero</label>
                <select onChange={handleChangeGenre} id="genero">
                    <option value="all" defaultValue>Todos</option>
                    {
                        allGenres && allGenres.map(genr => {
                            return (
                                <option key={genr.id} value={genr.name}>{genr.name}</option>
                            )
                        })
                    }
                </select>
            </div>
            <div></div>
        </section>
        </>
    )
}


export default Filters;
