import { useEffect, useState } from "react";
import axios from "axios";
import Card from "../Card/Card";
import "./App.css";
import SearchBar from "../Search/SearchBar";
const API = "http://localhost:3001/videogames?page=1";

const Home = () => {
  const [videoGames, setVideoGames] = useState([]);
  const getVideogames = async () => {
    const { data } = await axios.get(`${API}`);
    let isMounted = true;

    if (isMounted) {

      setVideoGames(data);
    }

    isMounted = false;
  };

  useEffect(() => {
    getVideogames();


  }, [setVideoGames]);

  return (
    <div className="App">
      <SearchBar />
      <main>
        {videoGames?.map((game) => {
          return (
            <Card
              key={game.id}
              id={game.id}
              name={game.nombre}
              image={game.imagen}
              plataforms={game.plataformas}
              genero={game.genero}
            />
          );
        })}
      </main>
    </div>
  );
};

export default Home;
