import React from "react";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Pinwheel } from "@uiball/loaders";

const Detail = () => {
  const [detail, setDetail] = useState({});
  const { id } = useParams();

  const getDetail = async (id) => {
    try {
      const { data } = await axios.get(
        `http://localhost:3001/videogames/${id}`
      );
      console.log(data);
      if (data.nombre) {
        setDetail(data);
      } else {
        window.alert("No se encontro el ID");
      }
    } catch (error) {
      console.log("Error detail axios: " + error.message);
    }
  };
console.log(detail);
  useEffect(() => {
    getDetail(3498);
  }, [id]);

  return (
    <div className="containerDetail">
      <h2>Detail Character</h2>
      {detail.id ? (
        <div className="cardDetail">
          <div>
            <h2>{detail.nombre}</h2>
            <h3>Descripcion: </h3>
            {detail.descripcion}
            <h3>Genero: </h3>
            <div>
              {detail.genero &&
                detail.genero.map((genr) => <p key={genr.id}>{genr.slug}</p>)}
            </div>
            <h3>Plataformas: </h3>
            {
              detail.plataformas?.map((plataforma, index) => {
                return <p key={index}>{plataforma}</p>
              })
            }
          </div>

          <img src={detail.imagen} alt={detail.nombre} />
        </div>
      ) : (
        <Pinwheel size={35} lineWeight={3.5} speed={1} color="black" />
      )}
    </div>
  );
};

export default Detail;
