import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { postCreateGame } from "../../redux/actions/actions.js";

const Form = ({ allGenres, allPlataforms }) => {
  //tengo que traerme todas los generos da la db almacenados
  //cada que se inicie la app me los tengo que traer.
  //los coloco en un estado global
  const [createGame, setCreateGame] = useState({
    nombre: "",
    imagen: "",
    descripcion: "",
    fecha_de_lanzamiento: "",
    plataformas: [],
    genero: [],
    rating: 0,
  });

  const dispatch = useDispatch();

  const onInputChange = (e) => {
    const { name, value } = e.target;

    setCreateGame({
      ...createGame,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (Object.values(createGame).length >= 5) {
      await dispatch(postCreateGame(createGame));
    }
  };

  //agregar a Plataformas
  const handleSelectGenre = (e) => {
    if(e.target.value !== "genero" && !createGame.genero.includes(e.target.value)) {
      setCreateGame({

            ...createGame,
            genero: [...createGame.genero, e.target.value]
        });
    }
};


  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="">Nombre: </label>
        <input
          type="text"
          onChange={onInputChange}
          name="nombre"
          value={createGame.nombre}
          placeholder="Nombre del juego..."
        />
        <label htmlFor="">Imagen: </label>
        <input
          type="text"
          onChange={onInputChange}
          name="imagen"
          value={createGame.imagen}
          placeholder="URL de la imagen..."
        />
        <label htmlFor="">Descripcion: </label>
        <input
          type="text"
          onChange={onInputChange}
          name="descripcion"
          value={createGame.descripcion}
          placeholder="Descripcion..."
        />
        {/* <label htmlFor="">Plataformas: </label>
        <input
          type="text"
          onChange={onInputChange}
          name="plataformas"
          value={createGame.plataformas}
          placeholder="Plataformas..."
        /> */}
        <select
          className="form-select "
          name="plataforms"
          onChange={onInputChange}
        >
          <option defaultValue>Seleccionar Plataforma</option>
          {allPlataforms?.map((plat, index) => {
            return (
              <option key={index + 1} value={plat.name}>
                {plat.name}
              </option>
            );
          })}
        </select>
        <label htmlFor="">Fecha de lanzamiento: </label>
        <input
          type="text"
          onChange={onInputChange}
          name="fecha_de_lanzamiento"
          value={createGame.fecha_de_lanzamiento}
          placeholder="Fecha de lanzamiento"
        />
        <label htmlFor="">Rating: </label>
        <input
          type="number"
          onChange={onInputChange}
          name="rating"
          value={createGame.rating}
          placeholder="Rating"
        />

        <select className="form-select " name="genero" onChange={handleSelectGenre}>
          <option defaultValue>Select Genero</option>
          {allGenres?.map((genre) => {
            return (
              <option key={genre.id} value={genre.name}>
                {genre.name}
              </option>
            );
          })}
        </select>

        <button type="submit">Save Game</button>
      </form>
    </div>
  );
};

export default Form;
