import { GET_VIDEOGAMES, GET_GAME_BY_NAME } from "../type/types";

const initialState = {
  allVideogames: [],
  allGamesOrigin: [],
};

const reducer = (state = initialState, action) => {
  // eslint-disable-next-line default-case
  switch (action.type) {
    case GET_VIDEOGAMES:
      return {
        ...state,
        allVideogames: action.payload,
        allGamesOrigin: action.payload,
      };
      case GET_GAME_BY_NAME:
        const getGameByName = state.allVideogames.filter( game => game.name === action.payload)
      return {
        ...state,
        allVideogames: [...getGameByName]
      }

      default: 
      return state ;
  }
};

export default reducer;
