import { useEffect, useState } from "react";
import { DotPulse } from "@uiball/loaders";
//import axios from "axios";
import Card from "../Card/Card";
import { useSelector, useDispatch } from "react-redux";
import "./App.css";
import SearchBar from "../Search/SearchBar";
import { getAllGames } from "../../redux/actions/actions";
import Filters from "../Filters/Filters";
//const API = "http://localhost:3001/videogames/";

const Home = () => {
  const [isTrue, setIsTrue] = useState(false);
  const [filterGames, setFilterGames] = useState({
    genero: "all",
    origen: "all",
  });

  const filteredVideogames = (arr) => {
    return arr.filter((game) => {
      return (
        (filterGames.genero === "all" ||
          game.genero.find((name) => name.toLowerCase() === filterGames.genero.toLowerCase())) &&
        (filterGames.origen === "all" || String(game.created) === filterGames.origen)
      );
    });
  };

  // Botones/Opciones para filtrar por género, y por si su origen es de la API o de la base
  // de datos (creados por nosotros desde el formulario).
  // Botones/Opciones para ordenar tanto ascendentemente como descendentemente
  // los videojuegos por orden alfabético y por rating.

  const allVideogames = useSelector((state) => state.allVideogames);
  const dispatch = useDispatch();
const newGamesFiltered = filteredVideogames(allVideogames);

//paginacion
  const [currentPage, setCurrentPage] = useState(1);
  const gamesPerPage = 15;

  // const firtsIndex = lastIndex - gamesPerPage;
  const firtsIndex = (currentPage - 1) * gamesPerPage;
  const filteredGames = newGamesFiltered;
  let lastIndex = currentPage * gamesPerPage;

  const numGames = filteredGames.length;

  if(lastIndex > numGames){
    lastIndex = numGames
  }
  const records = allVideogames.slice(firtsIndex, lastIndex);
  const numPage = Math.ceil(numGames / gamesPerPage);
  const numbers = [...Array(numPage + 1).keys()].slice(1);

  const prePage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const changeToPage = (id) => {
    setCurrentPage(id);
  };

  const nextPage = () => {
    if (currentPage !== numPage) {
      setCurrentPage(currentPage + 1);
    }
  };



  //ejecuta la funcion para mandar traer todo el array de elementos.
  useEffect(() => {
    setIsTrue(true);
    dispatch(getAllGames());
    if (records.lenght > 0) {
      setIsTrue(false);
    }
  }, [dispatch, records.length]);

  return (
    <div className="App">
      <SearchBar />
      <Filters onChangeFilters={setFilterGames} />
      <nav>
        <ul>
          <li>
            <button type="button" onClick={prePage}>
              Prev
            </button>
          </li>
          {numbers.map((n, i) => (
            <li key={i}>
              <span onClick={() => changeToPage(n)}>{n}</span>
            </li>
          ))}
          <li>
            <button type="button" onClick={nextPage}>
              Next
            </button>
          </li>
        </ul>
      </nav>
      {isTrue ? (
        <main>
          {newGamesFiltered?.map((game) => {
            return (
              <Card
                key={game.id}
                id={game.id}
                name={game.nombre}
                image={game.imagen}
                plataforms={game.plataformas}
                genero={game.genero}
              />
            );
          })}
        </main>
      ) : (
        <div>
          <DotPulse size={80} lineWeight={3.5} speed={1} color="white" />
        </div>
      )}
    </div>
  );
};

export default Home;
